import socket
import sys

def scan_port(host, port):
    try:
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sock.settimeout(1)
        result = sock.connect_ex((host, port))
        sock.close()
        return result == 0
    except:
        return False

host = input("Enter host: ")
for port in range(20, 100):
    if scan_port(host, port):
        print(f"Port {port} is open")
